    color: var(--color-secundario);
}

p {
    font-size: 1.8rem;
    line-height: 2;
}

.boton-color2 {
    background-color: var(--color-secundario);
    padding: 1rem 5rem;
    display: inline-block;
    margin-top: 2rem;
    color: white;
    text-decoration: none;
    font-weight: 200;
    font-size: 1.8rem;
    border-radius: .5rem;
}

.boton-color2:hover {
    background-color: var(--color-fondo);
    color: var(--color-secundario);
    font-weight: 400;
}

.contenedor {
    max-width: 1200px;
    margin: 0 auto;
    background-color: var(--color-fondo);
}


/* Barra de navegación principal */

.menu-principal {
    background-color: var(--color-principal);
}

.logo {
    padding: 2rem;
    background-color: var(--color-secundario);
}

.logo img {
    height: 3rem;
    display: block;
    margin-left: auto;
    margin-right: auto;
}

.menu-principal nav {
    max-width: 1200px;
    margin: 0 auto;
    padding: 2rem 0;
    display: flex;
    flex-direction: column;
    align-items: center;
