.menu-principal a:last-of-type {
    margin-bottom: 0;
}

.menu-principal a:hover {
    color: var(--color-fondo);
}

.activa {
    color: var(--color-fondo)!important;
}

.site-footer {
    background-color: var(--color-principal);
}

.site-footer a {
    text-decoration: none;
    color: var(--color-texto);
