    background-color: var(--color-secundario);
    padding: 1rem 5rem;
    display: inline-block;
    margin-top: 2rem;
    color: white;
    text-decoration: none;
    font-weight: 200;
    font-size: 1.8rem;
    border-radius: .5rem;
    border: 0.2rem solid var(--color-secundario);
}

.boton-color2:hover {
    background-color: var(--color-fondo);
    color: var(--color-secundario);
}

.boton-rrss {
    background-color: var(--color-secundario);
    padding: 0.5rem 1rem;
    display: inline-block;
    margin-top: 2rem;
